/**   create school_car databases*//** createdb -E utf8 -T template0 school_car ; */drop table schools;drop table classes;drop table cars;drop table students;drop table parents ;drop table rollcall/** create schools table */create table schools (       id serial primary key,       sid numeric(64,0) NOT NULL,       school_name character varying(255),       address character varying(255),       telephone character varying(255),       domain_name character varying(255),       username character varying(255),       passwd character varying(255),       status character varying(255),       valid integer DEFAULT 1,       logo character varying(255),       created_at timestamp without time zone DEFAULT now(),       updated_at timestamp without time zone) ;/** create classes table */create table classes (        id serial primary key,        clid numeric(64,0) NOT NULL,        name character varying(255),        teacher_name character varying(255),        teacher_headimg character varying(255),        telephone character varying(255),        description character varying(255),        username character varying(255),        passwd character varying(255),        school_id numeric(64,0),        created_at timestamp without time zone DEFAULT now(),       );/** create cars table */create table cars (       id serial primary key,       cid numeric(64,0) NOT NULL,       num character varying(255),       driver character varying(255),       driver_headimg character varying(255),       telephone character varying(255),       description character varying(255),       username character varying(255),       passwd character varying(255),       school_id numeric(64,0),       created_at timestamp without time zone DEFAULT now(),       updated_at timestamp without time zone       );/** create students table */create table students (       id serial primary key,       stuid numeric(64,0) NOT NULL,       name character varying(255),       sex character varying(255),       age integer DEFAULT 0,       classes_id numeric(64,0),       headimg character varying(255),       car_id numeric(64,0),       school_id numeric(64,0),       username character varying(255),       passwd character varying(255),       description character varying(255),       created_at timestamp without time zone DEFAULT now(),       updated_at timestamp without time zone       ) ;/** create parents table */create table parents (       id serial primary key,       pid numeric(64,0) NOT NULL,       school_id numeric(64,0),       student_id numeric(64,0),       name character varying(255),       sex character varying(255),       age integer DEFAULT 0,       username character varying(255),       passwd character varying(255),       parent_headimg character varying(255),       description character varying(255),       relationship character varying(255),       telephone character varying(255),       dqcode character varying(255),       created_at timestamp without time zone DEFAULT now(),       updated_at timestamp without time zone       );/** create rollcall table */create table rollcall (       id serial primary key,       rid numeric(64,0) NOT NULL,       stuid numeric(64,0),       stu_name character varying(255),       sex character varying(255),       age integer DEFAULT 0,       headimg character varying(255),       created_at timestamp without time zone DEFAULT now(),       updated_at timestamp without time zone,       description character varying(255),       status character varying(255) /** receive_up receive_down send_up send_down  */       );insert into schools (sid, school_name, address, telephone, domain_name, username, passwd, status, logo) values (1,'mingshan_school','heibei_handan','13811463960','mingshan.xinxiaoche.cn','mingshan','mingshan','1', '/logo/123.jpg') ;insert into classes (clid, name, teacher_name, telephone, description, username, passwd, school